INSERT INTO `wp_posts` VALUES ('56', '1', '2017-12-08 15:48:20', '0000-00-00 00:00:00', '这是首页章节的范例。首页章节可以是除了首页本身以外的任何其他页面，包括显示最新博客文章的页面。', '主页章节', '', 'auto-draft', 'closed', 'closed', '', '', '', '', '2017-12-08 15:48:20', '0000-00-00 00:00:00', '', '0', 'http://my.wordpress49.com/?page_id=56', '0', 'page', '', '0');
